'use strict';

const propCheck = require('../scripts/utils/propCheck');

module.exports = require('yargs')
    .completion('completion')
    .strict()
    .middleware([propCheck])
    .commandDir('cmds')
    .help()
    .options({
        version: {
            alias: 'v',
            describe: 'pager-cli package version'
        },
        help: {
            alias: 'h',
            describe: 'help'
        }
    }).argv;
