'use strict';

const { validateCommandExists } = require('../../scripts/utils/validators');

exports.command = 'create <command>';

exports.desc = 'Create <command>';

exports.builder = (yargs) => {

    yargs.middleware([validateCommandExists.bind(this, ['kubectl']), validateCommandExists.bind(this, ['sops'])]);
    return yargs.commandDir('kubectl');
};

exports.handler = function handler() {};
