'use strict';

const path = require('path');
const fs = require('fs');

const {
    root,
    workspace,
    paths
} = require('../../pager-cli-properties.js');

module.exports = function propCheck() {

    if (fs.existsSync(path.join(process.env.HOME, '.polarisclirc'))) {
        if (!fs.existsSync(root)) {
            console.error('Root directory not found');
            process.exit(1);
            return false;
        }

        if (!fs.existsSync(workspace)) {
            console.error('workspace directory not found');
            process.exit(1);
            return false;
        }

        if (!fs.existsSync(paths.pagercli)) {
            console.error('pager-cli directory not found');
            process.exit(1);
            return false;
        }
    }

    return true;
};
